  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'notes_vpc_r1',
	title: 'Creating a VPC',
	author: 'Taieb'
};
export const metadata = {
  "unversionedId": "notes_vpc_r1",
  "id": "notes_vpc_r1",
  "isDocsHomePage": false,
  "title": "Creating a VPC",
  "description": "export const StyledButton = withStyles({",
  "source": "@site/docs/notes_vpc_r1.mdx",
  "slug": "/notes_vpc_r1",
  "permalink": "/FAQ/docs/notes_vpc_r1",
  "version": "current"
};
  /* @jsx mdx */
import Button from "@material-ui/core/Button"
import { withStyles } from "@material-ui/core/styles";
export const rightToc = [{
  value: 'Creating a VPC',
  id: 'creating-a-vpc',
  children: [{
    value: 'Step 1: Create a VPC',
    id: 'step-1-create-a-vpc',
    children: []
  }, {
    value: 'Step 2: Add subnets',
    id: 'step-2-add-subnets',
    children: []
  }, {
    value: 'Step 3: Create EC2 and attach VPC to it',
    id: 'step-3-create-ec2-and-attach-vpc-to-it',
    children: []
  }, {
    value: 'Step 4: Create route table and associate it to subnet',
    id: 'step-4-create-route-table-and-associate-it-to-subnet',
    children: []
  }, {
    value: 'Step 5: Allow HTTP access with NACL',
    id: 'step-5-allow-http-access-with-nacl',
    children: []
  }]
}];
export const StyledButton = withStyles({
  root: {
    background: "linear-gradient(315deg, #fc9842 0%, #fe5f75 74%)",
    borderRadius: 7,
    border: 0,
    color: "inherit",
    height: 35,
    padding: "0 30px",
    boxShadow: "0 3px 5px 2px rgba(255, 105, 135, .3)"
  },
  label: {
    textTransform: "capitalize"
  }
})(Button);

const layoutProps = {
  rightToc,
StyledButton
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">



    <h2 {...{
      "id": "creating-a-vpc"
    }}>{`Creating a VPC`}</h2>
    <p>{`Step-by-step instructions on creating a VPC.`}</p>
    <h3 {...{
      "id": "step-1-create-a-vpc"
    }}>{`Step 1: Create a VPC`}</h3>
    <p><img alt={"VPC AWS"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!./../src/media/vpc25.png").default} /></p>
    <h3 {...{
      "id": "step-2-add-subnets"
    }}>{`Step 2: Add subnets`}</h3>
    <p>{`Two public and two private subnets across two AZs.`}</p>
    <p><img alt={"VPC AWS"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!./../src/media/vpc26.png").default} /></p>
    <h3 {...{
      "id": "step-3-create-ec2-and-attach-vpc-to-it"
    }}>{`Step 3: Create EC2 and attach VPC to it`}</h3>
    <p>{`Make sure to enable auto assign IPs for each public subnet before attaching them to EC2.`}</p>
    <p><img alt={"VPC AWS"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!./../src/media/vpc27.png").default} /></p>
    <p><img alt={"VPC AWS"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!./../src/media/vpc28.png").default} /></p>
    <h3 {...{
      "id": "step-4-create-route-table-and-associate-it-to-subnet"
    }}>{`Step 4: Create route table and associate it to subnet`}</h3>
    <p>{`One each for public and private`}</p>
    <p><img alt={"VPC AWS"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!./../src/media/vpc29.png").default} /></p>
    <p><img alt={"VPC AWS"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!./../src/media/vpc30.png").default} /></p>
    <p><strong parentName="p">{`Edit route for public route table to associate any request from 0.0.0.0 to igw`}</strong></p>
    <p><img alt={"VPC AWS"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!./../src/media/vpc31.png").default} /></p>
    <p>{`Now you should be able to connect to the EC2 instance through SSH. `}</p>
    <p>{`The public subnet is now also the `}<strong parentName="p">{`bastion host`}</strong>{`.`}</p>
    <h3 {...{
      "id": "step-5-allow-http-access-with-nacl"
    }}>{`Step 5: Allow HTTP access with NACL`}</h3>
    <p>{`Edit security group of EC2 (launch-wizard-xyz) to allow HTTP on port 80.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`# Sample Apache server
sudo su
yum install httpd -y
systemctl enable httpd
systemctl start httpd
echo "hello world" > /var/www/html/index.html
`}</code></pre>
    <p>{`This will display the page on EC2 URL.`}</p>
    <p>{`You can then explicitly deny it by setting a new inbound rule in NACL of the VPC.`}</p>
    <p><img alt={"VPC AWS"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!./../src/media/vpc32.png").default} /></p>
    <ol>
      <li parentName="ol">{`Rules are evaluated starting with the lowest numbered rule. As soon as a rule matches traffic, it's applied immediately regardless of any higher-numbered rule that may contradict it.`}</li>
      <li parentName="ol">{`In the above example, Rule 80 will be evaluated and then won't go further to Rule 100 or the default rule (*).`}</li>
    </ol>
    <StyledButton href="/docs/notes_vpc" mdxType="StyledButton">Back to VPC</StyledButton>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
