  /* @jsx mdx */
import Button from "@material-ui/core/Button"
import { withStyles } from "@material-ui/core/styles";
export const rightToc = [];
export const StyledButton = withStyles({
  root: {
    background: "linear-gradient(315deg, #fc9842 0%, #fe5f75 74%)",
    borderRadius: 7,
    border: 0,
    color: "inherit",
    height: 35,
    padding: "0 30px",
    boxShadow: "0 3px 5px 2px rgba(255, 105, 135, .3)"
  },
  label: {
    textTransform: "capitalize"
  }
})(Button);

const layoutProps = {
  rightToc,
StyledButton
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">



    <ol>
      <li parentName="ol">
        <p parentName="li">{`Multi AZ allows you to have an exact copy of your production database in another Availability Zone.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`It is active-passive`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`AWS handles the replication for you, so when your production database is written to, it's automatically synced with the standby (same DNS name).`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`In the case of failure/maintenance, RDS would automatically switch to the standby without admin intervention in case of following events:`}</p>
        <ol parentName="li">
          <li parentName="ol">{`AZ outage`}</li>
          <li parentName="ol">{`Primary DB instance fails`}</li>
          <li parentName="ol">{`DB instance server type is changed`}</li>
          <li parentName="ol">{`OS is going through patching`}</li>
          <li parentName="ol">{`Manual failover was initiated`}</li>
        </ol>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Its main objective is disaster-recovery than performance improvements, for which you'd need read-replicas.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Read replicas can be set as multi-AZ. Aurora has Multi-AZ in-built by default.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li"><strong parentName="p">{`Multi-AZ cannot serve read requests, they are not meant to be used as read replicas. They are only meant to be used as stand-by (except Aurora which can be both multi-AZ and read replica at the same time).`}</strong></p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Failover is automatically handled by RDS and it will flip the CNAME record to point to the standby instead.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`You can create a read replica as a Multi-AZ DB instance. Amazon RDS creates a standby of your replica in another Availability Zone for failover support for the replica. Creating your read replica as a Multi-AZ DB instance is independent of whether the source database is a Multi-AZ DB instance.`}</p>
      </li>
    </ol>
    <StyledButton href="/docs/notes_rds" mdxType="StyledButton">Back to RDS</StyledButton>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
